// maze images generated at: http://www.mazegenerator.net/

const images = [
    // 15 by 15 orthogonal maze
    {"xIn": 120, "yIn": 0, "src": "data:image/png;base64, "},
    
    // 20 by 20 orthogonal maze
    {"xIn": 152, "yIn": 0, "src": "data:image/png;base64, "},
    
    // 25 by 25 orthogonal maze
    {"xIn": 200, "yIn": 0, "src": "data:image/png;base64, "},
    
    // 30 by 30 orthogonal maze
    {"xIn": 230, "yIn": 0, "src": "data:image/png;base64, "},
    
    // 35 by 35 orthogonal maze
    {"xIn": 280, "yIn": 0, "src": "data:image/png;base64, "},
    
    // 40 by 20 orthogonal maze
    {"xIn": 312, "yIn": 0, "src": "data:image/png;base64, "}
  ];
  
  const pathPixels = [];
  const paintPixels = [];
  
  let done = false;
  
  
  // called when you press the [Traverse] button
  function traverse() {
    let i = document.getElementById("images").selectedIndex,
      img = images[i];
      
    findExit(img.xIn, img.yIn);
  }
  
  
  // called when you pick an image in the combo-box
  function loadImage(callback) {
    let i = document.getElementById("images").selectedIndex,
        src = images[i].src,
        img = new Image();
  
    // will be called after the image finishes loading
    img.onload = () => {  
      let canvas = document.getElementById('canvas'),
        ctx = canvas.getContext('2d');
        
      canvas.width = img.width;
      canvas.height = img.height;
      
      ctx.drawImage(img, 0, 0);
      
      if(callback) {
        callback();
      }
    }
  
    // load the image, will call onload event whenever ready
    img.src = src;
  }
  
  
  // called from traverse()
  function findExit(xIn, yIn) {
    
    // place your logic in here
    
    // find and paint the path that traverses the maze
    // you receive the maze entrance coordinates (xIn, yIn)
    
    
    document.getElementById("images").disabled = true;
    document.getElementById("traverse").disabled = true;
  
    
    requestAnimationFrame(paint);
    
    let canvas = document.getElementById('canvas'),
      ctx = canvas.getContext('2d'),
      imgData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    
    env.imgWidth = canvas.width;
    env.imgHeight = canvas.height;
    
    env.xIn = xIn;
    env.yIn = yIn;
    
    env.imgCtx = ctx;
    env.imgPixels = imgData.data;
    
    env.imgData = imgData;
    env.stack = [];
    
    // start crawling south
    if( crawl(xIn, yIn, "S") ) {
      env.stack.push([xIn, yIn]);
    } else {
      alert("Could not find maze exit!");
    }
  }
  
  
  // global "environment" object, holds useful info accessible from everywhere
  const env = {
    imgWidth: 0,
    imgHeight: 0,
    scanDepth: 15,
    
    xIn: 0,
    yIn: 0,
    
    imgCtx: null,
    imgPixels: [],
    pixel: null,
    
    stack: [],
    speed: 5
  };
  
  
  
  /*
    crawl in required heading, until one of the following happens:
      - you hit a wall
      - you run out of image (meaning you escaped from the maze)
      - the wall to your left or right opens up
        when that happens, you crawl recursively to that side... then forward... then to the other side
      
    at some point you'll reach the exit, and return True to unwind the recursion stack
  */
  function crawl(x, y, heading) {
    let prevLeftWall = false,
        prevRightWall = false,
        order = [], //= [heading, leftHeading(heading), rightHeading(heading)],
        {imgWidth, imgHeight, stack, scanDepth} = env;
        
    let xOrig = x,
      yOrig = y,
      reachedIntersection = false;
      
    while(  x <= imgWidth && x >= 0 && y <= imgHeight && y >= 0 ) { 
      if(isWall(x, y)) {
        unwind(xOrig, yOrig);
        return false;
      }
      
      
      pathPixels.push([x, y]);
      paintPixels.push([x, y, "red"]);
      
      
      let crtLeftWall = hasWallToLeft(x, y, heading),
          crtRightWall = hasWallToRight(x, y, heading);
        
      if(prevLeftWall && !crtLeftWall) {
        // opening to the left
        order = [leftHeading(heading)];
        
        if(!hasWallAhead(x, y, heading)) {
          order.push(heading);
        }
        
        if(!hasWallToRight(x, y, heading)) {
          order.push(rightHeading(heading));
        }
  
        reachedIntersection = true;
        break;
      }
      
      if(prevRightWall && !crtRightWall) {
        // opening to the right
        order = [rightHeading(heading)];
        
        if(!hasWallAhead(x, y, heading)) {
          order.push(heading);
        }
        
        if(!hasWallToLeft(x, y, heading)) {
          order.push(leftHeading(heading));
        }
        
        reachedIntersection = true;
        break;
      }
      
      // continue forward
      [prevLeftWall, prevRightWall] = [crtLeftWall, crtRightWall];
      [x, y] = jumpAhead(x, y, heading, 1);
    }
    
    
    if(reachedIntersection) {
      // jump to the "middle" of the intersection
      
      for(let i = 0; i < scanDepth / 2; i++) {
        [x, y] = jumpAhead(x, y, heading, 1);
        
        pathPixels.push([x, y]);
        paintPixels.push([x, y, "red"]);
      }
    }
    
    if(x >= imgWidth || x <= 0 || y >= imgHeight || y <= 0) {
      // escaped from the maze!
      stack.push([x, y]);
      done = true;
      
      return true;
    }
    
    for(let i = 0; i < order.length; i++) {
      if(crawl(x, y, order[i])) {
        stack.push([x, y]);
        return true;
      } else {
        unwind(x, y);
      }
    }
  
    return false;
  }
  
  
  // helper functions
  
  function jumpAhead(x, y, heading, distance) {
    let {imgWidth, imgHeight} = env;
    
    switch(heading) {
      case "E":
        x += distance;
        break;
        
      case "W":
        x -= distance;
        break;
        
      case "S":
        y += distance;
        break;
        
      case "N":
        y -= distance;
        break;
        
      default:
        throw "Unknown heading: " + heading;
    }
    
    x = Math.min(imgWidth, Math.max(0, x));
    y = Math.min(imgHeight, Math.max(0, y));
    
    return [x, y];
  }
  
  
  function hasWallToLeft(x, y, heading) {
    if(heading == "E") {
      return hasWallToNorth(x, y);
    }
    
    if(heading == "W") {
      return hasWallToSouth(x, y);
    }
    
    if(heading == "S") {
      return hasWallToEast(x, y);
    }
    
    if(heading == "N") {
      return hasWallToWest(x, y);
    }
  }
  
  
  function hasWallToRight(x, y, heading) {
    if(heading == "E") {
      return hasWallToSouth(x, y);
    }
    
    if(heading == "W") {
      return hasWallToNorth(x, y);
    }
    
    if(heading == "S") {
      return hasWallToWest(x, y);
    }
    
    if(heading == "N") {
      return hasWallToEast(x, y);
    }
  }
  
  
  function hasWallAhead(x, y, heading) {
    if(heading == "E") {
      return hasWallToEast(x, y);
    }
    
    if(heading == "W") {
      return hasWallToWest(x, y);
    }
    
    if(heading == "S") {
      return hasWallToSouth(x, y);
    }
    
    if(heading == "N") {
      return hasWallToNorth(x, y);
    }
  }
  
  
  function hasWallToEast(x, y) {
    let {scanDepth, imgWidth} = env;
    
    for(let i = 1; i <= scanDepth; i++) {
      if(x + i < imgWidth && isPixelBlack(x + i, y))
      {
        return true;
      }
    }
    
    return false;
  }
  
  
  function hasWallToWest(x, y) {
    let {scanDepth} = env;
    
    for(let i = 1; i <= scanDepth; i++) {
      if(x - i > 0 && isPixelBlack(x - i, y))
      {
        return true;
      }
    }
    
    return false;
  }
  
  
  function hasWallToSouth(x, y) {
    let {scanDepth, imgHeight} = env;
    
    for(let i = 1; i <= scanDepth; i++) {
      if(y + i < imgHeight && isPixelBlack(x, y + i))
      {
        return true;
      }
    }
    
    return false;
  }
  
  
  function hasWallToNorth(x, y) {
    let {scanDepth} = env;
    
    for(let i = 1; i <= scanDepth; i++) {
      if(y - i > 0 && isPixelBlack(x, y - i))
      {
        return true;
      }
    }
    
    return false;
  }
  
  
  function leftHeading(heading) {
    if(heading == "E") {
      return "N";
    }
    
    if(heading == "W") {
      return "S";
    }
    
    if(heading == "S") {
      return "E";
    }
    
    if(heading == "N") {
      return "W";
    }
  }
  
  
  function rightHeading(heading) {
    if(heading == "E") {
      return "S";
    }
    
    if(heading == "W") {
      return "N";
    }
    
    if(heading == "S") {
      return "W";
    }
    
    if(heading == "N") {
      return "E";
    }
  }
  
  
  function isWall(x, y) {
    return isPixelBlack(x, y);
  }
  
  
  function isPixelBlack(x, y) {
    let {imgWidth, imgPixels} = env,
      i = 4 * y * imgWidth + 4 * x;
    
    if(imgPixels[i] == 0 && imgPixels[i + 1] == 0 && imgPixels[i + 2] == 0) {
      return true;
    }
    
    return false;
  }
  
  
  function paintPixel(x, y, r, g, b) {
    let {imgCtx} = env,
      pixel = imgCtx.getImageData(x, y, 1, 1);
    
    pixel.data[0] = r;
    pixel.data[1] = g;
    pixel.data[2] = b;
    
    imgCtx.putImageData(pixel, x, y, 0, 0, 1, 1);
  }
  
  
  function paintExitPath() {
    let {stack} = env,
      [x0, y0] = stack.pop();
    
    while(stack.length > 0) {
      let [x, y] = stack.pop();
      line(x0, y0, x, y, 3, "rgba(255, 0, 0, 0.7)");
      
      [x0, y0] = [x, y];
    }
  }
  
  
  function line(x1, y1, x2, y2, width = 1, color = "rgba(255, 0, 0, 1)") {
    let {imgCtx} = env;
    
    imgCtx.lineWidth = width;
    imgCtx.strokeStyle = color;
  
    imgCtx.beginPath();
    imgCtx.moveTo(x1, y1);
    imgCtx.lineTo(x2, y2);
    imgCtx.stroke();
  }
  
  
  
  // auto-testing
  function autoTest(test) {
    if(!test) {
      return;
    }
    
    document.getElementById("images").selectedIndex = 2;
    document.getElementById("speed").value = 5;
    
    setSpeed(5);
  
    // load the image, and call the traverse logic
    loadImage(() => {
      traverse();
    });
  }
  
  
  try {
    autoTest(true);
  } catch(err) {
    console.log(err);
  }
  
  
  function paint() {
    if(paintPixels.length == 0 && done) {
      onDone();
      return;
    }
    
    let prevColor = undefined;
    
    for(let i = 0; i < env.speed; i++) {
      if(paintPixels.length == 0) {
        break;
      }
      
      let[x, y, color] = paintPixels.shift();
      
      if(prevColor === undefined) {
        prevColor = color;
      }
      
      let rgb = color == "red" ? [255, 0, 0] : [255, 255, 255];
      paintPixel(x, y, ...rgb);
      
      
      if(prevColor != color) {
        break;
      }
    }
  
    
    requestAnimationFrame(paint);
  }
  
  
  function unwind(xTo, yTo) {
    if(pathPixels.length == 0) {
      return;
    }
    
    let [x, y] = pathPixels.pop();
    
    while(x != xTo || y != yTo) {
      paintPixels.push([x, y, "white"]);
      [x, y] = pathPixels.pop();
    }
    
    pathPixels.push([xTo, yTo]);
  }
  
  
  function setSpeed(speed) {
    let lbl = document.getElementById("lblSpeed");
    lbl.innerHTML = `Speed: <b>${speed}</b>`;
    env.speed = speed;
  }
  
  
  function onDone() {
    paintExitPath();
    
    document.getElementById("images").disabled = false;
    document.getElementById("traverse").disabled = false;
  }
  
  function onPause(){
loadImage(() => valami());
  }


  function onTraverse() {
    loadImage(() => traverse());
  }